import React from "react";
import { Grid, Box, Card, makeStyles } from "@material-ui/core";
import "tachyons";
import "animate.css";

const useStyles = makeStyles({
  root: {
    backgroundColor: "#A63D40",
  },
  logo: {
    placeItems: "center",
    height: "75vh",
    color: "151515",
    "& h1": {
      color: "#E9B872",
    },
  },
  sub_text: {
    padding: "20px",
    borderRadius: "99px",
    backgroundColor: "#1db954",
    fontWeight: 600,
    color: "white",
    textDecoration: "none",
    "& hover": {
      backgroundColor: "white",
      borderColor: "#1db954",
      color: "#1db954",
    },
  },
  login_button: {
    padding: "20px",
    borderRadius: "99px",
    backgroundColor: "#1db954",
    fontWeight: 600,
    color: "white",
    textDecoration: "none",
    "& hover": {
      backgroundColor: "white",
      borderColor: "#1db954",
      color: "#1db954",
    },
  },
});

function Home() {
  const classes = useStyles();
  return (
    <Box className={classes.root}>
      <Grid container>
        <Grid item xs={12}>
          <div className={classes.logo}>
            <h1 class="f-subheadline lh-title code animate__animated animate__bounce animate__slower">
              explorify
            </h1>
          </div>
        </Grid>
        <Grid item xs={6}>
          <h2>Based on</h2>
        </Grid>
        <a
          href="/auth/login"
          class="ib link bw1 b--solid b--dark-blue dark-blue fw9 pa3 br3 hover-white hover-bg-dark-blue"
        >
          LOGIN WITH SPOTIFY
        </a>
      </Grid>
    </Box>
  );
}

export default Home;
