import React from "react";
import { Grid, Box, Card, makeStyles, Button, styled } from "@material-ui/core";
import CssBaseline from "@material-ui/core/CssBaseline";
import { ThemeProvider, createMuiTheme } from "@material-ui/core/styles";
import "tachyons";
import "animate.css";

const useStyles = makeStyles((login_theme) => ({
  root: {
    backgroundColor: "#A63D40",
  },
  logo: {
    placeItems: "center",
    color: "151515",
    "& h1": {
      color: "#E9B872",
    },
  },
  sub_text: {
    padding: "20px",
    borderRadius: "99px",
    backgroundColor: "#1db954",
    fontWeight: 600,
    color: "white",
    textDecoration: "none",
    "& hover": {
      backgroundColor: "white",
      borderColor: "#1db954",
      color: "#1db954",
    },
  },
  login_button: {
    padding: "20px",
    borderRadius: "99px",
    backgroundColor: "#1db954",
    fontWeight: 600,
    color: "white",
    textDecoration: "none",
    "& hover": {
      backgroundColor: "white",
      borderColor: "#1db954",
      color: "#1db954",
    },
  },
}));

const login_theme = createMuiTheme({
  palette: {
    background: "#A63D40",
  },
});

const LoginButton = styled(Button)({
  background:
    "linear-gradient(to right, #b2c9d4, #b8c4da, #c8bdd7, #d9b6ca, #e3b2b4)",
  border: 0,
  borderRadius: 10,
  boxShadow: "0 3px 5px 2px rgba(255, 105, 135, .5)",
  color: "black",
  fontSize: 20,
  fontWeight: 1000,
  // height: 60,
  padding: "0 30px",
});

function LoginPage() {
  const classes = useStyles();
  return (
    <ThemeProvider theme={login_theme}>
      <Box className={classes.root}>
        <Grid container style={{ placeContent: "center" }}>
          <Grid item xs={12}>
            <div className={classes.logo}>
              <h1 class="f-subheadline lh-title code animate__animated animate__bounce animate__slower">
                explorify
              </h1>
            </div>
          </Grid>
          <Grid item xs={6}>
            <h2>Based on</h2>
          </Grid>
          <LoginButton href="/auth/login">LOGIN WITH SPOTIFY</LoginButton>
        </Grid>
      </Box>
    </ThemeProvider>
  );
}

export default LoginPage;
