import React from "react";
import {
  Grid,
  makeStyles,
  Typography,
  List,
  ListItem,
  ListItemText,
  Avatar,
  Paper,
  Box,
  Card,
  CardContent,
} from "@material-ui/core";

const useStyles = makeStyles((theme) => ({
  gridContainer: {
    direction: "row",
    padding: 10,
  },
  cover_art: {
    marginLeft: -15,
    marginRight: 10,
    // width: theme.spacing(5.5),
    width: theme.spacing(10),
    // height: theme.spacing(5.5),
    height: theme.spacing(10),
  },
  track_name: {
    fontSize: 16,
    fontWeight: 500,
  },
  artist_name: {
    fontSize: 13,
  },
  paper_result: {
    maxWidth: "300px",
    maxHeight: "250px",
    // minHeight: "300px",
    marginBottom: 30,
    borderRadius: 10,
  },
}));

const RecommendationResults = ({ results }) => {
  console.log(results);
  const classes = useStyles();
  if (!results.tracks || results.tracks.length === 0) {
    return <Typography>No Results!</Typography>;
  }
  return (
    <Grid container className={classes.gridContainer}>
      <Grid container spacing={2}>
        {/* <List> */}
        {results.tracks.map((track) => (
          <Grid item align="center">
            <Card className={classes.paper_result}>
              <ListItem key={track.id}>
                <CardContent>
                  <Avatar
                    src={track.album.images[2].url}
                    variant="square"
                    className={classes.cover_art}
                  ></Avatar>
                </CardContent>
                <ListItemText>
                  <Typography className={classes.track_name}>
                    {track.name}
                  </Typography>
                  <Typography className={classes.artist_name}>
                    {track.artists[0].name}
                  </Typography>
                </ListItemText>
              </ListItem>
            </Card>
          </Grid>
        ))}
        {/* </List> */}
      </Grid>
    </Grid>
  );
};

export default RecommendationResults;
