import React from "react";
import {
  Grid,
  Box,
  Card,
  CardContent,
  CardMedia,
  CardActionArea,
  makeStyles,
  Button,
  styled,
  Typography,
} from "@material-ui/core";
import { ThemeProvider, createMuiTheme } from "@material-ui/core/styles";
import ExitToAppIcon from "@material-ui/icons/ExitToApp";
import NewsCard2Demo from "./description";

const useStyles = makeStyles((theme) => ({
  root: {
    flexGrow: 1,
  },
  title: {
    marginTop: 20,
    fontSize: "4.5em",
    fontFamily: "Kiwi Maru",
    fontWeight: 500,
  },
  desc_root: {
    maxWidth: 350,
    margin: "auto",
    borderRadius: 5,
    position: "relative",
  },
  desc_content: { padding: 24 },
  desc_cta: {
    display: "block",
    textAlign: "center",
    color: "#fff",
    letterSpacing: "3px",
    fontWeight: 200,
    fontSize: 12,
  },
  desc_title: { color: "#black", letterSpacing: "2px" },
}));

const theme = createMuiTheme({
  typography: {
    fontFamily: ["Kanit", "Kiwi Maru"],
  },
  background: {
    default: "#A63D40",
  },
});

const LoginButton = styled(Button)({
  background:
    "linear-gradient(to right, #b2c9d4, #b8c4da, #c8bdd7, #d9b6ca, #e3b2b4)",
  border: 0,
  borderRadius: 10,
  boxShadow: "0 3px 5px 2px rgba(255, 105, 135, .3)",
  color: "black",
  fontSize: 20,
  fontWeight: 400,
  height: 60,
  padding: "0 30px",
});

function LoginPage() {
  const classes = useStyles();
  return (
    <div className={classes.root}>
      <Grid
        container
        direction="column"
        justify="center"
        alignItems="center"
        spacing={5}
      >
        <Grid item>
          <Typography className={classes.title}>explorify</Typography>
        </Grid>
        <Grid item>
          <Card className={classes.desc_root}>
            <CardContent className={classes.desc_content}>
              <Typography className={classes.desc_title} variant="h5">
                What is this?
              </Typography>
              <Box
                display={"flex"}
                flexDirection={"column"}
                alignItems={"center"}
                justifyContent={"center"}
                minHeight={360}
                color={"common.white"}
                textAlign={"center"}
              >
                <h1 className={classes.desc_title}>Space</h1>
              </Box>
            </CardContent>
            {/* <CardMedia
              // classes={mediaStyles}
              image={
                "https://images.unsplash.com/photo-1519810755548-39cd217da494?ixlib=rb-1.2.1&ixid=eyJhcHBfaWQiOjEyMDd9&auto=format&fit=crop&w=934&q=80"
              }
            />
            <CardContent className={classes.desc_content}>
              <Box
                display={"flex"}
                flexDirection={"column"}
                alignItems={"center"}
                justifyContent={"center"}
                minHeight={360}
                color={"common.white"}
                textAlign={"center"}
              >
                <Typography className={classes.desc_title}>Space</Typography>
                <p>
                  The space between the stars and galaxies is largely empty.
                </p>
              </Box>
              <Typography className={classes.desc_cta} variant={"overline"}>
                Explore
              </Typography>
            </CardContent> */}
          </Card>
        </Grid>
        <Grid item>
          <ThemeProvider theme={theme}>
            <LoginButton href="/auth/login" endIcon={<ExitToAppIcon />}>
              LOGIN WITH SPOTIFY
            </LoginButton>
          </ThemeProvider>
        </Grid>
      </Grid>
    </div>
  );
}

export default LoginPage;
