import React, { useState } from "react";
import {
  makeStyles,
  Box,
  Card,
  CardContent,
  Grid,
  Typography,
  Button,
  ThemeProvider,
  createMuiTheme,
  CssBaseline,
} from "@material-ui/core";
import axios from "axios";

import SearchBar from "./component/search_bar";
import RecBoards from "./component/rec_boards";
import RecommendationResults from "./component/rec_results";
import GetUserData from "./component/get_user_data";

const theme = createMuiTheme({
  typography: {
    fontFamily: ["Inter", "Kiwi Maru"],
  },
  palette: {
    background: {
      default: "white",
    },
  },
  props: {
    MuiButtonBase: {
      disableRipple: true,
    },
  },
});

const useStyles = makeStyles((theme) => ({
  gridContainer: {
    padding: 10,
    spacing: 10,
    alignItems: "center",
    direction: "row",
  },
  nav_title: {
    padding: 10,
    display: "flex",
    flexDirection: "row",
    placeItems: "left",
    fontSize: "1.5rem",
    fontFamily: "Kiwi Maru",
  },
  step_root: {
    // align: "center",
    maxWidth: 1000,
    marginBottom: 50,
  },
  step_header: {
    color: "black",
    textAlign: "left",
    fontSize: "1.55em",
    marginBottom: 10,
    fontWeight: "600",
  },
  step_desc: {
    color: "black",
    textAlign: "left",
    fontSize: "1.1em",
    marginBottom: 20,
  },
}));

const Explorify = ({ auth }) => {
  const classes = useStyles();
  const { token } = auth;
  const [rec_values, set_rec_values] = useState([]);
  const [selected_artists, set_selected_artists] = useState([]);
  const [results, set_results] = useState(null);

  // console.log("sa", selected_artists);
  // console.log("rv", rec_values);
  // console.log("a", auth);

  const get_recommendations = async () => {
    const url = "https://api.spotify.com/v1/recommendations";
    let selected_artists_string;
    if (selected_artists_string < 0) {
      return;
    } else {
      selected_artists_string = `seed_artists=${selected_artists.join(",")}`;
    }
    let min = [];
    let max = [];
    Object.keys(rec_values).forEach((rec) => {
      if (rec_values[rec].enabled) {
        min.push(`min_${rec}=${rec_values[rec].value[0]}`);
        max.push(`min_${rec}=${rec_values[rec].value[1]}`);
      }
    });

    const min_string = min.join("&");
    const max_string = max.join("&");

    const { data } = await axios.get(
      `${url}?${selected_artists_string}&${min_string}&${max_string}`,
      {
        headers: {
          Authorization: `Bearer ${token}`,
        },
      }
    );
    console.log(data);
    set_results(data);
  };

  return (
    <ThemeProvider theme={theme}>
      <CssBaseline />
      <Grid container className={classes.gridContainer}>
        <Grid item xs={12}>
          <Typography className={classes.nav_title}>explorify</Typography>
        </Grid>
        <Grid item xs={12}>
          <GetUserData auth={auth}></GetUserData>
        </Grid>
        <Grid item xs={12} align="center">
          <Box>
            <Card className={classes.step_root}>
              <CardContent>
                <Typography className={classes.step_header}>
                  Step 1: Input Your Seeds
                </Typography>
                <Typography className={classes.step_desc}>
                  Include up to 5 "seeds", these seeds will serve as reference
                  tracks for the basis of your new set of recommended songs.{" "}
                  <br></br>You can search for track and artists.
                </Typography>
                <SearchBar
                  auth={auth}
                  onChange={set_selected_artists}
                ></SearchBar>
              </CardContent>
            </Card>
          </Box>
        </Grid>
        <Grid item xs={12} align="center">
          <Box>
            <Card className={classes.step_root}>
              <CardContent>
                <Typography className={classes.step_header}>
                  step 2: tune seed attributes
                </Typography>
                <Typography className={classes.step_desc}>
                  in this step, you have the ability to fine tune specific
                  attributes that you want to look for in your recommended
                  results <br></br> first, check the attribute you want to
                  modify <br></br> notice how each attribute has 2 sliders? this
                  allows for you to input a range!
                </Typography>
                <RecBoards onChange={set_rec_values} />
              </CardContent>
            </Card>
          </Box>
        </Grid>
        <Grid item xs={4}>
          <Button
            style={{
              margin: "auto",
              justify: "center",
              backgroundColor: "#1DB954",
              color: "white",
            }}
            variant={"contained"}
            onClick={get_recommendations}
          >
            Get Recommendations!
          </Button>
        </Grid>
        <Grid item xs={12}>
          {results && <RecommendationResults results={results} />}
        </Grid>
      </Grid>
    </ThemeProvider>
  );
};

export default Explorify;
