import React, { useState } from "react";
import {
  makeStyles,
  Box,
  Card,
  CardContent,
  Grid,
  Typography,
  Button,
  ThemeProvider,
  createMuiTheme,
  CssBaseline,
  Avatar,
} from "@material-ui/core";
import axios from "axios";

import SearchBar from "./component/search_bar";
import RecBoards from "./component/rec_boards";
import RecommendationResults from "./component/rec_results";
import GetUserData from "./component/get_user_data";
// import ExportToPlaylist from "./component/export_to_playlist";
// const querystring = require("querystring");
import acousticness from "./icon/acousticness.png";
import danceability from "./icon/danceability.png";
import instrumental from "./icon/instrumental.png";
import loudness from "./icon/loudness.png";
import loudness2 from "./icon/loudness2.png";
import popularity from "./icon/popularity.png";
import tempo from "./icon/tempo.png";
import valence from "./icon/valence.png";
import liveness from "./icon/liveness.png";
import speechiness from "./icon/speechiness.png";
import energy from "./icon/energy.png";

const theme = createMuiTheme({
  typography: {
    fontFamily: ["Inter", "Kiwi Maru"],
  },
  palette: {
    background: {
      default: "#6b705c",
    },
  },
  props: {
    MuiButtonBase: {
      disableRipple: true,
    },
  },
  typography: { allVariants: { color: "white" } },
  overrides: {
    MuiSlider: {
      root: {
        // color: "#3880ff",
        height: 5,
        padding: "15px 0",
        width: "95%",
        // "&$disabled": {
        //   color: "#e6e4df",
        // },
      },
      switchBase: {
        // Controls default (unchecked) color for the thumb
        // color: "#ccc",
      },
      colorSecondary: {
        "&$checked": {
          // Controls checked color for the thumb
          color: "#f2ff00",
        },
      },
      thumb: {
        height: 13,
        width: 13,
        // size: 50,
        // marginTop: -8,
        // marginLeft: -12,
        // color: "#2B2D25",
        border: "5px solid currentColor",
        "&:focus, &:hover, &$active": {
          boxShadow: "inherit",
        },
        "&$disabled": {
          opacity: 0.8,
          // color: "#e6e4df",
        },
      },
      track: {
        // color: "#2B2D25",
        height: 5,
        borderRadius: 4,
        // "&$disabled": {
        //   color: "#e6e4df",
        // },
      },
      rail: {
        color: "#FFE8D6",
        height: 5,
        borderRadius: 4,
        // "&$disabled": {
        //   color: "#e6e4df",
        // },
      },
      valueLabel: { left: "calc(-50% - 12px)", color: "#2B2D25" },
    },
  },
});

const useStyles = makeStyles((theme) => ({
  gridContainer: {
    padding: 10,
    spacing: 10,
    alignItems: "center",
    direction: "row",
  },
  gridItem: {
    backgroundColor: "#FFB4A2",
    background: "#000",
    color: "#000",
  },
  nav_title: {
    padding: 10,
    display: "flex",
    flexDirection: "row",
    placeItems: "left",
    fontSize: "1.5rem",
    fontFamily: "Kiwi Maru",
  },
  step_root: {
    // align: "center",
    padding: 10,
    maxWidth: 1000,
    marginBottom: 50,
    backgroundColor: "#ddbea9",
    borderRadius: 10,
  },
  step_header: {
    color: "black",
    textAlign: "left",
    fontSize: "1.55em",
    marginBottom: 10,
    fontWeight: "600",
  },
  step_desc: {
    color: "black",
    textAlign: "left",
    fontSize: "1.1em",
    marginBottom: 20,
  },
  card: {
    // backgroundColor: "FFB4A2",
  },
  card_content: {
    // backgroundColor: "#FFB4A2",
  },

  track_attribute_card_1: {
    width: "275px",
    height: "330px",
    marginBottom: 30,
    borderRadius: 10,
  },

  track_attribute_font_title: {
    color: "#000",
    fontFamily: "Benne",
    fontWeight: 700,
    fontSize: "1.7em",
  },
  track_attribute_font_title_2: {
    color: "#fff",
    fontFamily: "Benne",
    fontWeight: 700,
    fontSize: "1.7em",
  },
  track_attribute_font_type: {
    color: "#000",
    fontFamily: "Benne",
    fontWeight: 700,
    fontSize: "1.3em",
  },
  track_attribute_font_type_2: {
    color: "#fff",
    fontFamily: "Benne",
    fontWeight: 700,
    fontSize: "1.3em",
  },
  track_attribute_font_content: {
    color: "#000",
    fontFamily: "Benne",
    fontWeight: 700,
    fontSize: "1.2em",
  },
  track_attribute_font_content_2: {
    color: "#e6e4df",
    fontFamily: "Benne",
    fontWeight: 600,
    fontSize: "1.13em",
    textAlign: "left",
    // lineSpacing: "10",
  },
  track_attribute_image: {
    margin: 10,
    width: theme.spacing(9),
    height: theme.spacing(9),
  },
}));

const Explorify = ({ auth }) => {
  const classes = useStyles();
  const { token } = auth;

  const [user_id, set_user_id] = useState("");
  const [rec_values, set_rec_values] = useState([]);
  const [results, set_results] = useState(null);
  const [results_data, set_results_data] = useState([]);
  const [result_uri, set_result_uri] = useState("");
  const [selected_seeds, set_selected_seeds] = useState([]);
  const [playlist_id, set_playlist_id] = useState("");

  console.log("rv", rec_values);
  console.log("user_id", user_id);
  console.log("results");

  const get_recommendations = async () => {
    const url = "https://api.spotify.com/v1/recommendations";
    let seed_string;
    if (seed_string < 0) {
      return;
    } else {
      seed_string = `seed_tracks=${selected_seeds.join(",")}`;
    }
    let min = [];
    let max = [];
    Object.keys(rec_values).forEach((rec) => {
      if (rec_values[rec].enabled) {
        min.push(`min_${rec}=${rec_values[rec].value[0]}`);
        max.push(`min_${rec}=${rec_values[rec].value[1]}`);
      }
    });

    const min_string = min.join("&");
    const max_string = max.join("&");

    const { data } = await axios.get(
      `${url}?${seed_string}&${min_string}&${max_string}`,
      {
        headers: {
          Authorization: `Bearer ${token}`,
        },
      }
    );
    set_results(data);
    console.log("data", data, results);
    // const uri = results.tracks.map((r) => "spotify:track:" + r.id);
    // console.log(uri);
  };

  const create_playlist = async () => {
    const url = "https://api.spotify.com/v1/users/";
    const playlist_name = "brought to you by explorify";
    // const playlist_desc = "generated at ";
    return await axios
      .post(
        `${url}${user_id}/playlists`,
        JSON.stringify({
          name: playlist_name,
          // epd,
        }),
        {
          headers: {
            Authorization: `Bearer ${token}`,
            "Content-Type": "application/json",
          },
        }
      )
      .then((data) => set_playlist_id(data.data.id));
    // set_playlist_id(data.id);
    // return export_to_playlist();

    /* Add songs to playlist */
  };

  // const export_to_playlist = async () => {
  async function export_to_playlist() {
    // const playlist_id = await set_playlist_id;
    console.log(playlist_id);
    const url = "https://api.spotify.com/v1/playlists/";
    const uri = results.tracks.map((r) => "spotify:track:" + r.id);
    const euri = encodeURIComponent(uri);
    console.log(uri);
    return await axios
      .post(`${url}${playlist_id}/tracks?uris=${euri}`, null, {
        headers: {
          Authorization: `Bearer ${token}`,
          "Content-Type": "application/json",
        },
      })
      .then((data) => console.log("success", data));
    // if (data) {
    //   console.log("success", data);
    // }
  }

  function handle_create_and_export() {
    create_playlist();
    setTimeout(function () {
      export_to_playlist();
    }, 15000);
    console.log("pid", playlist_id);
    // export_to_playlist();
  }

  return (
    <ThemeProvider theme={theme}>
      <CssBaseline />
      <Grid container className={classes.gridContainer}>
        <Grid item xs={12} className="classes.gridItem">
          {/* <Typography className={classes.nav_title}>explorify</Typography> */}
        </Grid>
        <Grid item xs={12}>
          <GetUserData auth={auth} onChange={set_user_id}></GetUserData>
        </Grid>
        <Grid item xs={12} align="center">
          <Box className={classes.card}>
            <Card className={classes.step_root}>
              <CardContent className={classes.card_content}>
                <Typography className={classes.step_header}>
                  Step 1: Input Your Seeds
                </Typography>
                <Typography className={classes.step_desc}>
                  Include up to 5 "seeds", these seeds will serve as reference
                  tracks for the basis of your new set of recommended songs.{" "}
                  <br></br>Go ahead and search for some songs! When you are
                  done, scroll down to the next step.
                </Typography>
                <SearchBar
                  auth={auth}
                  onChange={set_selected_seeds}
                ></SearchBar>
              </CardContent>
            </Card>
          </Box>
        </Grid>
        <Grid item xs={12} align="center">
          <Box>
            <Card className={classes.step_root}>
              <CardContent>
                <Typography className={classes.step_header}>
                  Step 2: Fine Tune Your Track Attributes
                </Typography>
                <Typography
                  className={classes.step_desc}
                  style={{ paddingBottom: 15 }}
                >
                  In this step, you have the ability to fine tune specific track
                  attributes that you want to look for in your recommended
                  songs. <br></br> First learn about the attributes. Then,
                  scroll down and select the track attributes you want to
                  modify. <br></br>Lastly, drag the min or max sliders to really
                  specify your desired range!
                </Typography>
                <Grid
                  container
                  direction="row"
                  spacing={2}
                  style={
                    {
                      // paddingLeft: 45,
                    }
                  }
                  justify="center"
                  alignItems="center"
                >
                  <Grid item>
                    <Card
                      className={classes.track_attribute_card_1}
                      style={{ backgroundColor: "#BDBAD5" }}
                    >
                      <CardContent>
                        <Typography
                          className={classes.track_attribute_font_title}
                        >
                          acousticness
                        </Typography>
                        <Avatar
                          variant="square"
                          src={acousticness}
                          className={classes.track_attribute_image}
                        ></Avatar>
                        <CardContent>
                          <Typography
                            className={classes.track_attribute_font_type}
                          >
                            type: context
                          </Typography>
                          <Typography
                            className={classes.track_attribute_font_type}
                          >
                            the # of times
                          </Typography>
                        </CardContent>
                      </CardContent>
                    </Card>
                  </Grid>
                  <Grid item>
                    <Card
                      className={classes.track_attribute_card_1}
                      style={{ backgroundColor: "#124E78", color: "#fff" }}
                    >
                      <CardContent>
                        <Typography
                          className={classes.track_attribute_font_title_2}
                        >
                          danceability
                        </Typography>
                        <Avatar
                          variant="square"
                          src={danceability}
                          className={classes.track_attribute_image}
                        ></Avatar>
                        <CardContent>
                          <Typography
                            className={classes.track_attribute_font_type_2}
                          >
                            type: mood
                          </Typography>
                        </CardContent>
                      </CardContent>
                    </Card>
                  </Grid>
                  <Grid item>
                    <Card
                      className={classes.track_attribute_card_1}
                      style={{ backgroundColor: "#F49D6E" }}
                    >
                      <CardContent>
                        <Typography
                          className={classes.track_attribute_font_title}
                        >
                          energy
                        </Typography>
                        <Avatar
                          variant="square"
                          src={energy}
                          className={classes.track_attribute_image}
                        ></Avatar>
                        <CardContent>
                          <Typography
                            className={classes.track_attribute_font_type}
                          >
                            type: mood
                            <Typography
                              className={classes.track_attribute_font_content}
                            >
                              the measure of intensity & activity. higher energy
                              will restrict results to fast, loud, and noisy
                              songs.
                              <br></br>
                              pst: heavy metal -> high energy
                            </Typography>
                          </Typography>
                        </CardContent>
                      </CardContent>
                    </Card>
                  </Grid>
                  <Grid item>
                    <Card
                      className={classes.track_attribute_card_1}
                      style={{ backgroundColor: "#F2CD5D" }}
                    >
                      <CardContent>
                        <Typography
                          className={classes.track_attribute_font_title}
                        >
                          instrumentalness
                        </Typography>
                        <Avatar
                          variant="square"
                          src={instrumental}
                          className={classes.track_attribute_image}
                        ></Avatar>
                        <CardContent>
