import React, { useState, useEffect } from "react";
import axios from "axios";
import {
  makeStyles,
  Paper,
  Grid,
  TextField,
  Button,
  Typography,
} from "@material-ui/core";
import { Search } from "@material-ui/icons";
import SearchResults from "./search_results";

const useStyles = makeStyles({
  search: {
    display: "grid",
    placeItems: "center",
    height: "50vh",
  },
  search_bar: {
    display: "flex",
    "flex-direction": "row",
    maxWidth: 500,
    justify: "center",
    padding: 5,
    margin: "auto",
    "background-color": "white",
  },
  input_component: {
    height: "30px",
    width: "71px",
    border: "1px solid #D3D4D0",
    borderRadius: "5px",
    backgroundColor: "#FFFFFF",
    boxShadow: "0 1px 0 0 rgba(170,170,170,0.01)",
  },
  inputText: {
    color: "rgba(0,0,0,0.87)",
    fontSize: "16px",
    letterSpacing: "0.5px",
    lineHeight: "28px",
    textAlign: "center",
  },
});

export default function SearchBar({ auth, onChange }) {
  const classes = useStyles();
  const { token } = auth;

  const [search_result, set_search_result] = useState([]);
  const [search_string, set_search_string] = useState("");
  const [selected_artists, set_selected_artists] = useState([]);

  async function spotify_search_artists() {
    const url = "https://api.spotify.com/v1/search";
    const search_query = encodeURIComponent(search_string);
    console.log("search_query", search_query);
    const search_type_query = "type=artist";
    console.log("token", token);
    const { data } = await axios.get(
      `${url}?q=${search_query}&${search_type_query}`,
      {
        headers: {
          Authorization: `Bearer ${token}`,
        },
      }
    );
    console.log(data);
    if (data && data.artists) {
      set_search_result(data.artists.items);
    }
  }

  useEffect(() => {
    onChange(selected_artists);
  }, [onChange, selected_artists]);

  console.log("search_result", search_result);

  return (
    <Grid
      container
      style={{ "align-items": "flex-end" }}
      spacing={0}
      justify="center"
    >
      <Grid item xs={12}>
        <Paper elevation={3} className={classes.search_bar}>
          <TextField
            id={"outlined-basic"}
            variant={"outlined"}
            label={"Search for Artists"}
            inputProps={{ min: 0, style: { textAlign: "left" } }}
            InputProps={classes.inputText}
            fullWidth
            onChange={(event) => set_search_string(event.target.value)}
            value={search_string}
            color="#1DB954"
          ></TextField>
          <Button
            style={{ backgroundColor: "#1DB954" }}
            onClick={spotify_search_artists}
          >
            <Search />
          </Button>
        </Paper>
      </Grid>
      <Grid item xs={6}>
        {selected_artists.map((artist, index) => (
          <Typography>
            {index + 1}. {artist}
          </Typography>
        ))}
      </Grid>
      <Grid item xs={12}>
        <SearchResults
          results={search_result}
          onChange={set_selected_artists}
        />
      </Grid>
    </Grid>
  );
}
