import React, { useState } from "react";
import clsx from "clsx";
import {
  ListItem,
  List,
  ListItemIcon,
  ListItemText,
  Checkbox,
  makeStyles,
  Typography,
  Avatar,
  Grid,
} from "@material-ui/core";

const useStyles = makeStyles((theme) => ({
  checkboxes_root: {
    "&:hover": {
      backgroundColor: "transparent",
    },
  },
  checkboxes_icon: {
    borderRadius: 3,
    width: 20,
    height: 20,
    boxShadow:
      "inset 0 0 0 1px rgba(16,22,26,.2), inset 0 -1px 0 rgba(16,22,26,.1)",
    backgroundColor: "#f5f8fa",
    backgroundImage:
      "linear-gradient(180deg,hsla(0,0%,100%,.8),hsla(0,0%,100%,0))",
    "$root.Mui-focusVisible &": {
      outline: "2px auto rgba(19,124,189,.6)",
      outlineOffset: 2,
    },
    "input:hover ~ &": {
      backgroundColor: "#ebf1f5",
    },
    "input:disabled ~ &": {
      boxShadow: "none",
      background: "rgba(206,217,224,.5)",
    },
  },
  checked_icon: {
    backgroundColor: "#137cbd",
    backgroundImage:
      "linear-gradient(180deg,hsla(0,0%,100%,.1),hsla(0,0%,100%,0))",
    "&:before": {
      display: "block",
      width: 20,
      height: 20,
      backgroundImage:
        "url(\"data:image/svg+xml;charset=utf-8,%3Csvg xmlns='http://www.w3.org/2000/svg' viewBox='0 0 16 16'%3E%3Cpath" +
        " fill-rule='evenodd' clip-rule='evenodd' d='M12 5c-.28 0-.53.11-.71.29L7 9.59l-2.29-2.3a1.003 " +
        "1.003 0 00-1.42 1.42l3 3c.18.18.43.29.71.29s.53-.11.71-.29l5-5A1.003 1.003 0 0012 5z' fill='%23fff'/%3E%3C/svg%3E\")",
      content: '""',
    },
    "input:hover ~ &": {
      backgroundColor: "#106ba3",
    },
  },
  grid_container: {
    direction: "row",
    alignItems: "center",
    justifyContent: "center",
  },
  search_results: {
    // paddingTop: 15,
    marginTop: 15,
  },
  cover_art: {
    marginLeft: -15,
    marginRight: 10,
    width: theme.spacing(5.5),
    height: theme.spacing(5.5),
  },
  track_name: {
    fontSize: 16,
    fontWeight: 500,
  },
  artist_name: {
    fontSize: 13,
  },
}));

export default function SearchResults({ results, onChange }) {
  const classes = useStyles();
  const [checked, set_checked] = useState([]);
  const [seeds, set_seeds] = useState([]);
  console.log(checked);

  const handle_toggle = (value) => () => {
    const cur_index = checked.indexOf(value);
    const new_checked = [...checked];
    if (cur_index === -1) {
      if (checked.length < 5) {
        new_checked.push(value);
      }
    } else {
      new_checked.splice(cur_index, 1);
    }
    set_checked(new_checked);
    onChange(new_checked);
  };

  function handle_seeds(item) {
    const new_seed = [...seeds];
    set_seeds(item);
  }

  return (
    <Grid container className={classes.grid_container}>
      <Grid item xs={12} sm={6} className={classes.search_results}>
        <List>
          {results.map((item, index) => (
            <ListItem
              key={item.id}
              button
              onClick={(handle_toggle(item.id), handle_seeds(item))}
              // margin={-4}
            >
              <ListItemIcon>
                <Checkbox
                  className={classes.checkboxes_root}
                  checkedIcon={
                    <span
                      className={clsx(
                        classes.checkboxes_icon,
                        classes.checked_icon
                      )}
                    />
                  }
                  icon={<span className={classes.checkboxes_icon} />}
                  inputProps={{ "aria-label": "decorative checkbox" }}
                  disableRipple={true}
                  edge={"start"}
                  checked={checked.indexOf(item.id) !== -1}
                  tabIndex={-1}
                />
              </ListItemIcon>
              <Avatar
                src={item.album.images[2].url}
                variant="square"
                className={classes.cover_art}
              ></Avatar>

              <ListItemText>
                <Typography className={classes.track_name}>
                  {item.name}
                </Typography>
                <Typography className={classes.artist_name}>
                  {item.artists[0].name}
                </Typography>
              </ListItemText>
            </ListItem>
          ))}
        </List>
      </Grid>
    </Grid>
  );
}
