import React, { useState, useEffect } from "react";
import {
  makeStyles,
  Box,
  Card,
  Paper,
  Grid,
  TextField,
  Button,
  Typography,
} from "@material-ui/core";
import axios from "axios";
import "tachyons";

export default function GetUserData({ auth }) {
  const { token } = auth;
  console.log("user_data", token);
  const [user_data, set_user_data] = useState(null);
  const [user_name, set_user_name] = useState("");
  const [user_image, set_user_image] = useState("");

  async function retrieve_data() {
    const url = "https://api.spotify.com/v1/me";
    const { data } = await axios.get(`${url}`, {
      headers: {
        Authorization: `Bearer ${token}`,
      },
    });
    console.log(data);
    if (data) {
      set_user_data(data);
      set_user_name(data.display_name);
      set_user_image(data.images[0].url);
    }
  }

  function handle_retrieve_data() {
    set_user_data(data);
    set_user_name(data.display_name);
    set_user_image(data.images[0].url);
  }
  console.log("user_name", user_name);
  console.log("user_image", user_image);
}
