import React from "react";
import {
  Grid,
  makeStyles,
  Typography,
  List,
  ListItem,
  ListItemText,
  Avatar,
  Paper,
  Box,
  Card,
  CardContent,
  CardMedia,
} from "@material-ui/core";
import ReactFitText from "react-fittext";

const useStyles = makeStyles((theme) => ({
  gridContainer: {
    direction: "row",
    padding: 10,
    justifyContent: "center",
    alignItems: "center",
  },
  gridContainer2: {
    direction: "row",
    padding: 10,
    justifyContent: "center",
    alignItems: "center",
    marginTop: 10,
  },
  cover_art: {
    width: theme.spacing(9),
    height: theme.spacing(9),
  },
  track_name: {
    // fontSize: "10",
    position: "absolute",
    top: "20%",
    width: "100%",
    fontSize: "1em",
    fontWeight: 600,
    textAlign: "center",
    backgroundColor: "none",
  },
  artist_name: {
    fontSize: 13,
    textAlign: "center",
  },
  paper_result: {
    position: "relative",
    width: "200px",
    height: "200px",
    // minHeight: "300px",
    marginBottom: 30,
    borderRadius: 10,
  },
}));

const RecommendationResults = ({ results }) => {
  console.log("results", results);
  const classes = useStyles();

  // const return_track_name = ({ track_name }) => {
  //   console.log("track_name", track_name);
  //   return track_name;
  // };

  if (!results.tracks || results.tracks.length === 0) {
    return (
      <Typography>
        Sorry, I couldn't find any results! Your modifications were too
        specific! Try selecting less track attributes or less referencee tracks.
      </Typography>
    );
  }

  return (
    <Grid container className={classes.gridContainer}>
      <Grid container spacing={2} className={classes.gridContainer2}>
        {results.tracks.map((track) => (
          <Grid item align="center">
            <Card
              className={classes.paper_result}
              style={{ background: track.album.images[2].url }}
            >
              <CardMedia
                style={{ position: "relative" }}
                component="img"
                image={track.album.images[2].url}
                style={{
                  opacity: 0.6,
                }}
              ></CardMedia>
              <CardContent
                styles={
                  {
                    // alignItems: "center",
                    // align: "center",
                    // justify: "center",
                  }
                }
              >
                <Typography className={classes.track_name}>
                  {track.name}
                </Typography>
                <Typography noWrap className={classes.artist_name}>
                  {track.artists[0].name}
                </Typography>
              </CardContent>
            </Card>
          </Grid>
        ))}
      </Grid>
    </Grid>
  );
};

export default RecommendationResults;
