import React from "react";
import {
  Grid,
  Box,
  Card,
  makeStyles,
  Button,
  styled,
  Typography,
} from "@material-ui/core";
import CssBaseline from "@material-ui/core/CssBaseline";
import { ThemeProvider, createMuiTheme } from "@material-ui/core/styles";
import "tachyons";
import "animate.css";
import ExitToAppIcon from "@material-ui/icons/ExitToApp";
import intro from "./font/intro.woff";

const useStyles = makeStyles((theme) => ({
  root: {
    flexGrow: 1,
  },
  title: {
    marginTop: 20,
    fontSize: "4.2em",
    fontFamily: "Kiwi Maru",
    fontWeight: 500,
  },
  sub_text: {
    padding: "20px",
    borderRadius: "99px",
    backgroundColor: "#1db954",
    fontWeight: 600,
    color: "white",
    textDecoration: "none",
    "& hover": {
      backgroundColor: "white",
      borderColor: "#1db954",
      color: "#1db954",
    },
  },
  login_button: {
    padding: "20px",
    borderRadius: "99px",
    backgroundColor: "#1db954",
    fontWeight: 600,
    color: "white",
    textDecoration: "none",
    "& hover": {
      backgroundColor: "white",
      borderColor: "#1db954",
      color: "#1db954",
    },
  },
}));

const theme = createMuiTheme({
  typography: {
    fontFamily: ["Kanit", "Kiwi Maru"],
  },
  background: {
    default: "#A63D40",
  },
});

const LoginButton = styled(Button)({
  background:
    "linear-gradient(to right, #b2c9d4, #b8c4da, #c8bdd7, #d9b6ca, #e3b2b4)",
  border: 0,
  borderRadius: 10,
  boxShadow: "0 3px 5px 2px rgba(255, 105, 135, .3)",
  color: "black",
  fontSize: 20,
  fontWeight: 400,
  height: 60,
  padding: "0 30px",
});

function LoginPage() {
  const classes = useStyles();
  return (
    <div className={classes.root}>
      <Grid
        container
        direction="column"
        justify="center"
        alignItems="center"
        spacing={5}
      >
        <Grid item>
          <Typography className={classes.title}>explorify</Typography>
        </Grid>
        <Grid item>
          <ThemeProvider theme={theme}>
            <LoginButton href="/auth/login" endIcon={<ExitToAppIcon />}>
              LOGIN WITH SPOTIFY
            </LoginButton>
          </ThemeProvider>
        </Grid>
      </Grid>
    </div>
    // <ThemeProvider theme={theme}>
    //     <Grid container style={{ placeContent: "center" }}>
    //       <Grid item xs={6}>
    //         <div className={classes.logo}>
    //           <h1 class="f-subheadline lh-title code animate__animated animate__bounce animate__slower">
    //             explorify
    //           </h1>
    //         </div>
    //       </Grid>
    //       <Grid item xs={6}>
    //         <h2>Based on</h2>
    //       </Grid>
    //     </Grid>
    //     {/* </Box> */}
    //   </ThemeProvider>
    // );
  );
}

export default LoginPage;
