import React, { useState } from "react";
import {
  makeStyles,
  Box,
  Card,
  CardContent,
  Grid,
  Typography,
  Button,
  ThemeProvider,
  createMuiTheme,
  CssBaseline,
  styled,
} from "@material-ui/core";
import axios from "axios";

import SearchBar from "./component/search_bar";
import RecBoards from "./component/rec_boards";
import RecommendationResults from "./component/rec_results";
import GetUserData from "./component/get_user_data";
import BookOfTrackAttributes from "./component/book_track_attribute";
import SendIcon from "@material-ui/icons/Send";

const theme = createMuiTheme({
  typography: {
    allVariants: {
      fontFamily: ["Inter", "Kiwi Maru"],
      color: "black",
    },
  },
  palette: {
    background: {
      default: "#6b705c",
    },
  },
  props: {
    MuiButtonBase: {
      disableRipple: true,
    },
  },
  overrides: {
    MuiSlider: {
      root: {
        // color: "#3880ff",
        height: 5,
        padding: "15px 0",
        width: "95%",
        // "&$disabled": {
        //   color: "#e6e4df",
        // },
      },
      switchBase: {
        // Controls default (unchecked) color for the thumb
        // color: "#ccc",
      },
      colorSecondary: {
        "&$checked": {
          // Controls checked color for the thumb
          color: "#f2ff00",
        },
      },
      thumb: {
        height: 13,
        width: 13,
        // size: 50,
        // marginTop: -8,
        // marginLeft: -12,
        // color: "#2B2D25",
        border: "5px solid currentColor",
        "&:focus, &:hover, &$active": {
          boxShadow: "inherit",
        },
        "&$disabled": {
          opacity: 0.8,
          // color: "#e6e4df",
        },
      },
      track: {
