import React, { useState, useEffect } from "react";
import LoginPage from "./pre_pages/login_page";
import Loading from "./pre_pages/loading";
import Explorify from "./explorify";
import SnackbarProvider from "react-simple-snackbar";
import Dashboard from "./dashbord";

// 'URLSearchParams(window.location.search)' will get url string after the '?' & .get() will get the code value from the url
const code = new URLSearchParams(window.location.search).get("code");

function App() {
  return (
    <div className="app">
      {code ? <Dashboard code={code} /> : <LoginPage />}
    </div>
  );
}

export default App;
