import React from "react";
import {
  Grid,
  Box,
  Card,
  CardContent,
  CardMedia,
  CardActionArea,
  makeStyles,
  Button,
  styled,
  Typography,
} from "@material-ui/core";
import { ThemeProvider, createMuiTheme } from "@material-ui/core/styles";
import ExitToAppIcon from "@material-ui/icons/ExitToApp";
import CssBaseline from "@material-ui/core/CssBaseline";

const useStyles = makeStyles((theme) => ({
  root: {
    flexGrow: 1,
  },
  title: {
    marginTop: 20,
    fontSize: "4.5em",
    fontFamily: "Kiwi Maru",
    fontWeight: 500,
    // color: "#9EB56F",
  },
  desc_root: {
    maxWidth: 500,
    margin: "auto",
    borderRadius: 5,
    position: "relative",
    boxShadow: "0 3px 5px 2px rgba(0, 0, 0, .1)",
  },
  desc_content: { padding: 20 },
  desc_cta: {
    display: "block",
    textAlign: "justify",
    color: "black",
    fontSize: 15,
  },
  desc_title: {
    color: "#black",
    letterSpacing: "1.3px",
    fontSize: 30,
    textAlign: "left",
    marginTop: 5,
  },
}));

const theme = createMuiTheme({
  typography: {
    fontFamily: ["Kanit", "Kiwi Maru"],
  },
  palette: {
    background: {
      default: "#E3EAD6",
    },
  },
});

const LoginButton = styled(Button)({
  background:
    "linear-gradient(to right, #b2c9d4, #b8c4da, #c8bdd7, #d9b6ca, #e3b2b4)",
  border: 0,
  borderRadius: 10,
  boxShadow: "0 3px 5px 2px rgba(255, 105, 135, .3)",
  color: "black",
  fontSize: 20,
  fontWeight: 400,
  height: 45,
  padding: "0 30px",
});

function LoginPage() {
  const classes = useStyles();
  return (
    <ThemeProvider theme={theme}>
      <CssBaseline />
      <div className={classes.root}>
        <Grid
          container
          direction="column"
          justify="center"
          alignItems="center"
          spacing={5}
        >
          <Grid item>
            <Typography className={classes.title}>explorify</Typography>
          </Grid>
          <Grid item>
            <Card className={classes.desc_root}>
              <CardContent className={classes.desc_content}>
                <Typography className={classes.desc_title} variant="h5">
                  What is this?
                </Typography>
                <Box
                  display={"flex"}
                  flexDirection={"column"}
                  alignItems={"center"}
                  justifyContent={"center"}
                  minHeight={250}
                  color={"black"}
                  textAlign={"center"}
                  elevation={3}
                >
                  <Typography className={classes.desc_cta}>
                    Spotify provides developers a Recommendation API that can be
                    found{" "}
                    <a
                      href="https://developer.spotify.com/console/get-recommendations/"
                      target="_blank"
                      rel="noreferrer"
                    >
                      here
                    </a>
                    , disguised away from the public audience. Essentially, this
                    tool aims to unleash the potential of their API. Provided
                    your favorite artists and songs, Explorify aims to recommend
                    you other similar tracks (if there is sufficient
                    information). But, the cool thing is their API also allows
                    you to customize your final matches with specific track
                    attributes such as: danceability, energy, popularity, tempo,
                    and more. Give it a try!
                  </Typography>
                </Box>
                <Typography className={classes.desc_cta}>
                  Check me out at:{" "}
                  <a
                    href="https://www.xinwang.me"
                    target="_blank"
                    rel="noreferrer"
                  >
                    https://www.xinwang.me
                  </a>
                </Typography>
              </CardContent>
            </Card>
          </Grid>
          <Grid item>
            <LoginButton href="/auth/login" endIcon={<ExitToAppIcon />}>
              LOGIN WITH SPOTIFY
            </LoginButton>
          </Grid>
        </Grid>
      </div>
    </ThemeProvider>
  );
}

export default LoginPage;
