import React from "react";
import { Grid, Box, Card, makeStyles, Button, styled } from "@material-ui/core";
import CssBaseline from "@material-ui/core/CssBaseline";
import { ThemeProvider, createMuiTheme } from "@material-ui/core/styles";
import "tachyons";
import "animate.css";
import ExitToAppIcon from "@material-ui/icons/ExitToApp";
import intro from "./font/intro.woff";
const useStyles = makeStyles((login_theme) => ({
  root: {
    backgroundColor: "#A63D40",
  },
  logo: {
    placeItems: "center",
    color: "151515",
    "& h1": {
      color: "#E9B872",
    },
  },
  sub_text: {
    padding: "20px",
    borderRadius: "99px",
    backgroundColor: "#1db954",
    fontWeight: 600,
    color: "white",
    textDecoration: "none",
    "& hover": {
      backgroundColor: "white",
      borderColor: "#1db954",
      color: "#1db954",
    },
  },
  login_button: {
    padding: "20px",
    borderRadius: "99px",
    backgroundColor: "#1db954",
    fontWeight: 600,
    color: "white",
    textDecoration: "none",
    "& hover": {
      backgroundColor: "white",
      borderColor: "#1db954",
      color: "#1db954",
    },
  },
}));

const raleway = {
  fontFamily: "Raleway",
  fontStyle: "normal",
  fontDisplay: "swap",
  fontWeight: 400,
  src: `
    local('Raleway'),
    local('Raleway-Regular'),
    url(${RalewayWoff2}) format('woff2')
  `,
  unicodeRange:
    "U+0000-00FF, U+0131, U+0152-0153, U+02BB-02BC, U+02C6, U+02DA, U+02DC, U+2000-206F, U+2074, U+20AC, U+2122, U+2191, U+2193, U+2212, U+2215, U+FEFF",
};
const login_theme = createMuiTheme({
  typography: { fontFamily: "Noto" },
  palette: {
    background: "#A63D40",
  },
});

const LoginButton = styled(Button)({
  background:
    "linear-gradient(to right, #b2c9d4, #b8c4da, #c8bdd7, #d9b6ca, #e3b2b4)",
  border: 0,
  borderRadius: 10,
  boxShadow: "0 3px 5px 2px rgba(255, 105, 135, .5)",
  color: "black",
  fontSize: 23,
  fontWeight: 1000,
  // height: 60,
  padding: "0 30px",
});

function LoginPage() {
  const classes = useStyles();
  return (
    <ThemeProvider theme={login_theme}>
      <Box className={classes.root}>
        <Grid container style={{ placeContent: "center" }}>
          <Grid item xs={12}>
            <div className={classes.logo}>
              <h1 class="f-subheadline lh-title code animate__animated animate__bounce animate__slower">
                explorify
              </h1>
            </div>
          </Grid>
          <Grid item xs={6}>
            <h2>Based on</h2>
          </Grid>
          <LoginButton href="/auth/login" startIcon={<ExitToAppIcon />}>
            LOGIN WITH SPOTIFY
          </LoginButton>
        </Grid>
      </Box>
    </ThemeProvider>
  );
}

export default LoginPage;
