import React, { useState } from "react";
import { Grid, TextField, Button } from "@material-ui/core";
import { Search } from "@material-ui/icons";
import axios from "axios";
import SearchBar from "./component/search_bar";

const Explorify = ({ auth }) => {
  const { token } = auth;
  const [search_result, set_search_result] = useState([]);
  const [search_string, set_search_string] = useState("");

  const search_spotify = async () => {
    const url = "https://api.spotify.com/v1/search";
    const searchQuery = encodeURIComponent(search_string);
    const typeQuery = `type=artist`;
    const { data } = await axios.get(`${url}?q=${searchQuery}&${typeQuery}`, {
      headers: {
        Authorization: `Bearer ${token}`,
      },
    });
    if (data && data.artists) {
      set_search_result(data.artists.items);
    }
  };

  return (
    <div className={"App"}>
      <Grid container style={{ padding: 20 }} spacing={1}>
        <Grid item xs={12}>
          Explorify
        </Grid>
        <SearchBar auth={token}></SearchBar>
      </Grid>
    </div>
  );
};

export default Explorify;
