import React, { useState, useEffect } from "react";
import axios from "axios";
import {
  makeStyles,
  Paper,
  Grid,
  TextField,
  Button,
  Typography,
  Box,
} from "@material-ui/core";
import { Search } from "@material-ui/icons";
import SearchResults from "./search_results";

const useStyles = makeStyles({
  root: {
    marginTop: "5px",
    justifyContent: "center",
    alignItems: "center",
  },
  search_bar: {
    display: "flex",
    "flex-direction": "row",
    maxWidth: 500,
    justify: "center",
    padding: 7,
    margin: "auto",
    "background-color": "white",
    borderRadius: 5,
  },
  search_result: {
    paddingTop: 10,
    marginTop: 20,
  },
  selected_seeds: {
    paddingTop: 10,
    marginTop: 20,
    maxWidth: 350,
  },
  selected_seeds_header: {
    fontSize: 18,
    // paddingTop: 3,
    // marginTop: 20,
    // maxWidth: 350,
  },
});

export default function SearchBar({ auth, onChange }) {
  const classes = useStyles();
  const { token } = auth;

  const [search_result, set_search_result] = useState([]);
  const [search_string, set_search_string] = useState("");
  const [seleceted_seeds, set_selected_seeds] = useState([]);

  async function spotify_search_songs() {
    const url = "https://api.spotify.com/v1/search";
    const search_query = encodeURIComponent(search_string);
    console.log("search_query", search_query);
    const search_type_query = "type=track";
    console.log("token", token);
    const { data } = await axios.get(
      `${url}?q=${search_query}&${search_type_query}`,
      {
        headers: {
          Authorization: `Bearer ${token}`,
        },
      }
    );
    console.log(data);
    if (data && data.tracks) {
      set_search_result(data.tracks.items);
    }
  }

  useEffect(() => {
    onChange(seleceted_seeds);
  }, [onChange, seleceted_seeds]);

  console.log("search_result", search_result);

  return (
    <Grid
      container
      // style={{ "align-items": "flex-end" }}
      spacing={0}
      justify="center"
    >
      <Grid item xs={12}>
        <Box className={classes.search_bar}>
          <TextField
            id={"outlined-basic"}
            variant={"outlined"}
            label={"search for seeds"}
            placeholder="enter reference tracks"
            fullWidth
            InputLabelProps={{
              shrink: true,
            }}
            onChange={(e) => set_search_string(e.target.value)}
            onKeyDown={(e) => {
              console.log(e.key);
              if (e.key === "Enter") {
                spotify_search_songs();
              }
            }}
            value={search_string}
          ></TextField>
          <Button
            style={{
              marginLeft: 5,
              backgroundColor: "#1DB954",
              color: "white",
            }}
            onClick={spotify_search_songs}
            variant="contained"
          >
            <Search />
          </Button>
        </Box>
      </Grid>
      <Grid item xs={12} sm={6}>
        <Box className={classes.search_results}>
          <SearchResults
            results={search_result}
            onChange={set_selected_seeds}
          />
        </Box>
      </Grid>
      <Grid item xs={12} sm={6}>
        <Paper className={classes.selected_seeds}>
          <Typography className={classes.selected_seeds_header}>
            reference tracks
          </Typography>
          {seleceted_seeds.map((songs, index) => (
            <Typography>
              {index + 1}. {songs}
            </Typography>
          ))}
        </Paper>
      </Grid>
    </Grid>
  );
}
